mapper.select:
SELECT DISTINCT v.patient_no, pt.patient_name, pt.patient_phonenumber, o.order_code, cn.result_no, r.register_dt, s.sample_name, p.prescribe_dt, i.inspection_name, cn.figures, i.unit, i.baseline, cn.note FROM patient as pt, visit as v, prescribe as p, order_slip as o, collecting as cg, conclusion as cn, inspection_type as i, vessel as vl, classification as c, register as r, sample_type as s
where left(register_dt, 10) between #{startDate} and #{endDate}
where prescribe_dt between #{startDate} and #{endDate}
AND v.patient_no = #{patientNo} AND p.status_code = "M" AND o.order_code = #{orderSelect} AND pt.patient_no = v.patient_no AND v.visit_code = p.visit_code AND p.order_code = o.order_code AND o.vessel_code = vl.vessel_code AND p.prescribe_code = cg.prescribe_code AND o.classification_code = c.classification_code AND cg.barcode = r.barcode AND r.register_code = cn.register_code AND i.inspection_code = cn.inspection_code AND vl.sample_code = s.sample_code AND o.order_code = i.order_code ORDER BY r.register_dt DESC;

mapper.update:
UPDATE smsdata
SET sms_title = #{smsTitle}, sms_content = #{smsContent}
WHERE sms_no = #{smsNo}
